parameters.text:
=文章标题：{{ $('RSS Read').item.json.title }}
文章链接：{{ $('RSS Read').item.json.link }}
文章内容：'''
{{ $json.content }}'''

parameters.html:
={{ 
  $json.output.map((item, index) => `
    <div style="margin: 10px 0;">
      <span style="
        background: #2196f3;
        color: white;
        padding: 2px 8px;
        border-radius: 2px;
        margin-right: 8px;
      ">${index + 1}</span>
      ${item}
      <hr style="border: 1px solid #eee; margin-top: 8px;">
    </div>
  `).join('') 
}}
